INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (4492, 11849, 'Lorlatinib', 'Central Nervous System Diseases', 'A broad spectrum of central nervous system (CNS) effects can occur in patients receiving lorlatinib.  These include seizures, hallucinations, changes in cognitive function, mood (including suicidal ideation), speech, mental status, and sleep.  Overall, CNS effects occurred in as many as 54% of patients receiving lorlatinib with some requiring treatment discontinuation.  Caution and close monitoring of patients with history of seizures or any mental or psychiatric illness is recommended.', '2', '"Product Information. Lorbrena (lorlatinib)." Pfizer U.S. Pharmaceuticals Group  (2018):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (4493, 13897, 'Lorlatinib', 'Central Nervous System Diseases', 'A broad spectrum of central nervous system (CNS) effects can occur in patients receiving lorlatinib.  These include seizures, hallucinations, changes in cognitive function, mood (including suicidal ideation), speech, mental status, and sleep.  Overall, CNS effects occurred in as many as 54% of patients receiving lorlatinib with some requiring treatment discontinuation.  Caution and close monitoring of patients with history of seizures or any mental or psychiatric illness is recommended.', '2', '"Product Information. Lorbrena (lorlatinib)." Pfizer U.S. Pharmaceuticals Group  (2018):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (4494, 11849, 'Lorlatinib', 'Hepatic Insufficiency', 'The recommended dose of lorlatinib has not been established for patients with moderate or severe hepatic impairment, therefore, caution is advised if using in these patients.  Additionally, severe hepatotoxicity has been reported in patients with risk factors for hepatotoxicity (e.g., taking hepatotoxic drugs such as strong CYP3A inducers).  Caution and close monitoring of hepatic function is advised.', '2', '"Product Information. Lorbrena (lorlatinib)." Pfizer U.S. Pharmaceuticals Group  (2018):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (4495, 13897, 'Lorlatinib', 'Hepatic Insufficiency', 'The recommended dose of lorlatinib has not been established for patients with moderate or severe hepatic impairment, therefore, caution is advised if using in these patients.  Additionally, severe hepatotoxicity has been reported in patients with risk factors for hepatotoxicity (e.g., taking hepatotoxic drugs such as strong CYP3A inducers).  Caution and close monitoring of hepatic function is advised.', '2', '"Product Information. Lorbrena (lorlatinib)." Pfizer U.S. Pharmaceuticals Group  (2018):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (4496, 11849, 'Lorlatinib', 'Hyperglycemia', 'The use of lorlatinib might cause hyperglycemia.  It is recommended to assess and control fasting serum glucose prior to initiation of treatment and monitor periodically thereafter.  Caution should be exercised when treating diabetic patients.  Withhold and resume at a reduced dose or permanently discontinue treatment based on severity.', '2', '"Product Information. Lorbrena (lorlatinib)." Pfizer U.S. Pharmaceuticals Group  (2018):', 'DDInter', 0);
