INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (4514, 22082, 'Loxapine', 'Alcoholic Intoxication', 'The use of neuroleptic agents is contraindicated in patients with acute alcohol intoxication exhibiting depressed vital signs.  The central nervous system depressant effects of neuroleptic agents may be additive with those of alcohol.  Severe respiratory depression and respiratory arrest may occur.  Therapy with neuroleptic agents should be administered cautiously in patients who might be prone to acute alcohol intake.', '3', '"Product Information. Haldol (haloperidol)." McNeil Pharmaceutical  (2002):|"Product Information. Navane (thiothixene)." Roerig Division  (2001):|"Product Information. Clozaril (clozapine)." Novartis Pharmaceuticals  (2001):|"Product Information. Risperdal (risperidone)." Janssen Pharmaceuticals  (2001):|"Product Information. Zyprexa (olanzapine)." Lilly, Eli and Company  (2001):|"Product Information. Seroquel (quetiapine)." Astra-Zeneca Pharmaceuticals  (2001):|"Product Information. Moban (molindone)." Gate Pharmaceuticals  (2001):|"Product Information. Orap (pimozide)." Gate Pharmaceuticals|"Product Information. Geodon (ziprasidone)." Pfizer U.S. Pharmaceuticals  (2001):|"Product Information. Loxitane C (loxapine)." Apothecon Inc  (2022):', 'DDInter', 0);
