INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (2097, 15058, 'Leucovorin', 'Seizures', 'Seizures have occurred rarely during administration of leucovorin, primarily in patients receiving high dose leucovorin or leucovorin in combination with 5-fluorouracil, or in patients with a history of seizures or central nervous system abnormalities (unspecified).  Folic acid in large amounts may counter the anticonvulsant effects of phenobarbital, phenytoin, and pimozide.  Clinical monitoring of seizure activity is recommended and modification of anticonvulsant therapy may be necessary.', '2', '"Product Information. Wellcovorin (leucovorin)." Glaxo Wellcome, Research Triangle Park, NC.', 'DDInter', 0);
