INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (2932, 6419, 'Antithymocyte immunoglobulin (rabbit)', 'Infections', 'The use of anti-thymocyte globulin [rabbit] is contraindicated in patients who have active acute or chronic infections that contraindicate any additional immunosuppression.  To prevent over-immunosuppression, physicians may wish to decrease the dose of the maintenance immunosuppression regimen during the period of therapy use.  Low counts of platelets and white blood cells (including low counts of lymphocytes and neutrophils) have been identified and are reversible following dose adjustments.  It is recommended to monitor patients carefully and administer appropriate anti-infective treatment when indicated.  Also, total white blood cell and platelet counts should be monitored.', '3', '"Product Information. Thymoglobulin (rabbit) (anti-thymocyte globulin (rabbit))." Genzyme Corporation', 'DDInter', 0);
