INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, source) VALUES (3413, 0, 'Sulfasalazine', 'Intestinal Obstruction', 'The use of sulfasalazine is contraindicated in patients with intestinal obstruction.  Approximately one-third of orally administered sulfasalazine is systemically absorbed from the small intestine, while the majority is metabolized by intestinal bacteria to 5-aminosalicylic acid (5-ASA) that remains in the colonic lumen and gets excreted in the feces.', '3', 'DDInter');
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, source) VALUES (3414, 0, 'Sulfasalazine', 'Urethral Obstruction', 'The use of sulfasalazine tablets is contraindicated in patients with urinary obstruction.', '3', 'DDInter');
