INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7172, 2971, 'Methylphenidate', 'Hematologic Diseases', 'Hematologic toxicity, including thrombocytopenia, easy bruisability, epistaxis, leukopenia, anemia and eosinophilia, has been reported rarely during use of methylphenidate.  However, a causal relationship has not been established.  Therapy with methylphenidate (racemic) or dexmethylphenidate (the more pharmacologically active d-enantiomer) should be administered cautiously in patients with preexisting blood dyscrasias or bone marrow suppression.  Periodic hematologic monitoring may be appropriate in all patients during prolonged therapy.', '2', '"Product Information. Ritalin (methylphenidate)." Novartis Pharmaceuticals  (2001):|"Product Information. Focalin (dexmethylphenidate)." Mikart Inc  (2001):', 'DDInter', 0);
