INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7394, 142, 'Methyldopa', 'Anemia, Hemolytic', 'Hemolytic anemia has occurred rarely during therapy with methyldopa and may be associated with a (+) direct Coombs.  Therapy with methyldopa should be administered cautiously to patients with anemia or a predisposition to hemolytic syndromes.  Clinical monitoring of hematopoietic function, including a baseline hematological profile, is recommended.  If Coombs- positive hemolytic anemia occurs, the cause may be methyldopa and the drug should be discontinued', '2', 'Distenfeld A, Florita C, Gelfand ML "Hemolytic anemia induced by alpha-methyldopa." N Y State J Med Feb (1970):  570-3|Nelson RB Jr, Nelson RB III "Methyldopa-associated intravascular hemolysis." Arch Intern Med 137 (1977):  1260-1|Roy A, Ghosh ML "Coombs positive haemolytic anaemia due to methyldopa." Br J Clin Pract 35 (1981):  54, 58|"Product Information. Aldomet (methyldopa)." Merck & Co., Inc  (2001):', 'DDInter', 0);
