INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7412, 142, 'Methyldopa', 'Pheochromocytoma', 'Methyldopa causes fluorescence in urine samples at the same wave lengths as catecholamines, falsely high levels of urinary catecholamines may be reported.  This will interfere with the diagnosis of pheochromocytoma.  It is important to recognize this phenomenon before a patient with a possible pheochromocytoma is subjected to surgery.  Methyldopa is not recommended for the treatment of patients with pheochromocytoma.', '2', 'Cregler LL, Mark H "Second-degree atrioventricular block and alpha-methyldopa: a probable connection." Mt Sinai J Med 54 (1987):  168-70|Rosen B, Ovsyshcher IA, Zimlichman R "Complete atrioventricular block induced by methyldopa." Pacing Clin Electrophysiol 11 (1988):  1555-8|"Product Information. Aldomet (methyldopa)." Merck & Co., Inc  (2001):', 'DDInter', 0);
