INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7436, 7196, 'Methyltestosterone', 'Breast Neoplasms', 'When treated with androgenic anabolic steroids, patients with metastatic breast cancer may develop hypercalcemia due to osteolysis.  Frequent determination of urine and serum calcium levels is recommended during therapy with androgenic agents.  If hypercalcemia occurs, therapy should be stopped.  The development of hypercalcemia may indicate progression of metastases to the bone.', '3', '"Product Information. Halotestin (fluoxymesterone)." Pharmacia and Upjohn  (2001):|"Product Information. Androderm (testosterone topical)." SmithKline Beecham  (2001):', 'DDInter', 0);
