INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7484, 516, 'Metoclopramide', 'Depressive Disorder', 'Metoclopramide has been reported to cause mental depression in patients with and without a prior history of depression.  Symptoms have ranged from mild to severe and have included suicidal ideation and suicide.  Therapy with metoclopramide in patients with a history of mental depression, especially suicidal tendencies, should be administered cautiously, and only if benefits are anticipated to outweigh the potential risks.  Some patients who experience depression with metoclopramide have improved following temporary discontinuation of the drug and reinstitution at a lower dosage with gradual titration.', '3', 'Adams CD "Metoclopramide and depression." Ann Intern Med 103 (1985):  960|Bottner RK, Tullio CJ "Metoclopramide and depression." Ann Intern Med 103 (1985):  482|Feder R "Metoclopramide and depression." J Clin Psychiatry 48 (1987):  38|Weddington WW, Jr  Banner A "Organic affective syndrome associated with metoclopramide: case report." J Clin Psychiatry 47 (1986):  208-9|"Product Information. Reglan (metoclopramide)." Wyeth-Ayerst Laboratories  (2001):', 'DDInter', 0);
