INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7637, 516, 'Metoclopramide', 'Kidney Diseases', 'Renal function affects the clearance of metoclopramide.  In patients with varying degrees of renal impairment, a reduction in creatinine clearance was correlated with a reduction in plasma clearance, renal clearance, and non-renal clearance, and increase in elimination half-life of metoclopramide.  Therapy with metoclopramide should be administered cautiously in patients with impaired renal function.  A dosage reduction may be appropriate to avoid drug accumulation.', '2', 'Lehmann CR, Heironimus JD, Collins CB, O''Neil TJ, Pierson WP, Crowe JT, Melikian AP, Wright GJ "Metoclopramide kinetics in patients with impaired renal function and clearance by hemodialysis." Clin Pharmacol Ther 37 (1985):  284-9|Lloveras J, Masramon J, Aubia J, Llorach M "Amiodarone, metoclopramide, and renal failure." Lancet 1 (1979):  981-2|Caralps A "Metoclopramide and renal failure." Lancet 1 (1979):  554|Bateman DN, Gokal R, Dodd TR, Blain PG "The pharmacokinetics of single doses of metoclopramide in renal failure." Eur J Clin Pharmacol 19 (1981):  437-41|"Product Information. Reglan (metoclopramide)." Wyeth-Ayerst Laboratories  (2001):', 'DDInter', 0);
