INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7725, 0, 'Acebutolol', 'Diseases requiring hemodialysis', 'Acebutolol is moderately removed by hemodialysis and should be administered after dialysis when the patient is hemodynamically stable to avoid marked falls in blood pressure.  The hemodynamic status should be closely monitored before and after the dose.', '3', 'Roux A, Aubert P, Guedon J, Flouvat B "Study of acebutolol dialysis and pharmacokinetic data in patients with renal insufficiency undergoing hemodialysis." Nouv Presse Med 4 (1975):  3228-33|"Product Information. Sectral (acebutolol)." Wyeth-Ayerst Laboratories  (2002):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7726, 0, 'Acebutolol', 'Kidney Diseases', 'The active metabolite of acebutolol, diacetolol, is eliminated primarily by the kidney.  Patients with renal impairment may be at greater risk for adverse effects due to metabolite accumulation.  Dosage adjustments are recommended for patients with moderate to severe renal dysfunction (CrCl < 50 mL/min).', '3', 'Kirch W, Kohler H, Berggren G, Braun W "The influence of renal function on plasma levels and urinary excretion of acebutolol and its main N-acetyl metabolite." Clin Nephrol 18 (1982):  88-94|Gabriel R "Acebutolol in the management of hypertension in patients with renal disease." Br J Clin Pract 33 (1979):  259-62|Roux A, Aubert P, Guedon J, Flouvat B "Pharmacokinetics of acebutolol in patients with all grades of renal failure." Eur J Clin Pharmacol 17 (1980):  339-48|"Product Information. Sectral (acebutolol)." Wyeth-Ayerst Laboratories  (2002):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7727, 0, 'Acebutolol', 'Atrioventricular Block', 'The use of beta-adrenergic receptor blocking agents (aka beta-blockers) is contraindicated in patients with sinus bradyarrhythmia or heart block greater than the first degree (unless a functioning pacemaker is present).  Due to their negative inotropic and chronotropic effects on the heart, the use of beta-blockers is likely to exacerbate these conditions.', '3', 'Crean PA, Williams DO "Effect of intravenous and oral acebutolol in patients with bundle branch block." Int J Cardiol 10 (1986):  119-26|Mashford ML, Coventry D, Hecker R, et al. "Adverse Drug Reactions Advisory Committee: ADRAC report for 1980." Med J Aust 1 (1982):  416-9|Treseder AS, Thomas TP "Sinus arrest due to timolol eye drops." Br J Clin Pract 40 (1986):  256-8|"Product Information. Sectral (acebutolol)." Wyeth-Ayerst Laboratories  (2002):|"Product Information. Tenormin (atenolol)." ICN Pharmaceuticals Inc  (2002):|"Product Information. Normodyne (labetalol)." Schering Corporation  (2002):|"Product Information. Trandate (labetalol)." Glaxo Wellcome  (2002):|"Product Information. Corgard (nadolol)." Bristol-Myers Squibb  (2002):|"Product Information. Inderal (propranolol)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Blocadren (timolol)." Merck & Co., Inc  (2001):|"Product Information. Brevibloc (esmolol)." DuPont Pharmaceuticals  (2001):|"Product Information. Cartrol (carteolol)." Abbott Pharmaceutical  (2001):|"Product Information. Kerlone (betaxolol)." Searle  (2001):|"Product Information. Lopressor (metoprolol)." Novartis Pharmaceuticals  (2001):|"Product Information. Betapace (sotalol)." Berlex Laboratories  (2001):|"Product Information. Zebeta (bisoprolol)." Lederle Laboratories  (2001):|"Product Information. Visken (pindolol)." Sandoz Pharmaceuticals Corporation  (2001):|"Produ