INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7942, 7520, 'Metoprolol', 'Hyperthyroidism', 'When beta-adrenergic receptor blocking agents (aka beta-blockers) are used to alleviate symptoms of hyperthyroidism such as tachycardia, anxiety, tremor and heat intolerance, abrupt withdrawal can exacerbate thyrotoxicosis or precipitate a thyroid storm.  To minimize this risk, cessation of beta-blocker therapy, when necessary, should occur gradually with incrementally reduced dosages over a period of 1 to 2 weeks.  Patients should be advised not to discontinue treatment without first consulting with the physician.  Close monitoring is recommended during and after therapy withdrawal.', '2', '"Product Information. Sectral (acebutolol)." Wyeth-Ayerst Laboratories  (2002):|"Product Information. Tenormin (atenolol)." ICN Pharmaceuticals Inc  (2002):|"Product Information. Normodyne (labetalol)." Schering Corporation  (2002):|"Product Information. Trandate (labetalol)." Glaxo Wellcome  (2002):|"Product Information. Corgard (nadolol)." Bristol-Myers Squibb  (2002):|"Product Information. Inderal (propranolol)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Blocadren (timolol)." Merck & Co., Inc  (2001):|"Product Information. Cartrol (carteolol)." Abbott Pharmaceutical  (2001):|"Product Information. Kerlone (betaxolol)." Searle  (2001):|"Product Information. Lopressor (metoprolol)." Novartis Pharmaceuticals  (2001):|"Product Information. Betapace (sotalol)." Berlex Laboratories  (2001):|"Product Information. Zebeta (bisoprolol)." Lederle Laboratories  (2001):|"Product Information. Visken (pindolol)." Sandoz Pharmaceuticals Corporation  (2001):|"Product Information. Levatol (penbutolol)." Reed and Carnrick  (2001):|"Product Information. Coreg (carvedilol)." SmithKline Beecham  (2001):|"Product Information. Bystolic (nebivolol)." Forest Pharmaceuticals  (2007):', 'DDIn