INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (3551, 5730, 'Linagliptin', 'Pancreatitis', 'There have been postmarketing reports of acute pancreatitis in patients taking DPP-4 inhibitors.  These drugs should be used with caution in patients with a history of pancreatitis or pancreatic disease, although it is unknown if they are at increased risk.  Patients should be observed for signs and symptoms of pancreatitis during treatment.  If pancreatitis is suspected, treatment should be discontinued immediately and appropriate management should be initiated.', '3', '"Product Information. Januvia (sitagliptin)." Merck & Co., Inc  (2006):|"Product Information. Onglyza (saxagliptin)." Bristol-Myers Squibb  (2009):|"Product Information. Tradjenta (linagliptin)." Boehringer Ingelheim  (2011):|"Product Information. Nesina (alogliptin)." Takeda Pharmaceuticals America  (2013):', 'DDInter', 0);
