INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (3702, 27247, 'Lixisenatide', 'Angioedema', 'The use of lixisenatide may result in cross-sensitivity in patients with a history of anaphylaxis reaction or angioedema to another GLP-1 receptor agonist.  These patients should be informed of the risk of anaphylaxis when using lixisenatide.  Close monitoring is recommended when prescribing lixisenatide to these patients.', '3', '"Product Information. Adlyxin (lixisenatide)." sanofi-aventis  (2016):', 'DDInter', 0);
