INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (3829, 5798, 'Lithium carbonate', 'Diarrhea', 'The risk for lithium toxicity may be increased in patients with diarrhea and excessive sweating.  Dehydration and sodium depletion may occur in these patients leading to lithium retention and toxicity.  The use of lithium may need to be reduced or suspended until the condition resolves.  Supplemental fluid and salt may need to be administered.  Monitoring serum lithium levels is recommended.', '3', '"Product Information. Eskalith (lithium)." SmithKline Beecham  (2002):|"Product Information. Lithobid (lithium)." Ciba-Geigy Pharmaceuticals  (2002):', 'DDInter', 0);
