INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (4275, 5593, 'Acarbose', 'Intestinal Diseases', 'The use of alpha-glucosidase inhibitors is contraindicated in patients with inflammatory bowel disease, colonic ulceration, partial intestinal obstruction, or any chronic intestinal disease associated with marked disorders of digestion or absorption.  Alpha-glucosidase inhibitors competitively inhibit enzymes involved in the digestion of carbohydrates.  Increased gas formation in the intestines due to fermentation of the undigested carbohydrates can worsen or aggravate intestinal problems.', '3', 'Hollander P "Safety profile of acarbose, an alpha-glucosidase inhibitor." Drugs 44 Suppl 3 (1992):  47-53|Clissold SP, Edwards C "Acarbose. A preliminary review of its pharmacodynamic and pharmacokinetic properties, and therapeutic potential." Drugs 35 (1988):  214-43|"Product Information. Precose (acarbose)." Bayer  (2001):|Nishii Y, Aizawa T, Hashizume K "Ileus: a rare side effect of acarbose." Diabetes Care 19 (1996):  1033|"Product Information. Glyset (miglitol)." Bayer  (2001):', 'DDInter', 0);
