INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (4498, 11849, 'Lorlatinib', 'Hyperlipidemias', 'Increases in serum cholesterol and triglycerides have been reported in patients receiving lorlatinib.  Grade 3 or 4 elevations in total cholesterol occurred in 17% and Grade 3 or 4 elevations in triglycerides occurred in 17% of the patients enrolled in a clinical trial with up to 80% percent of patients requiring initiation of lipid-lowering medications.  Caution is advised in patients with hyperlipidemias.  Monitor serum cholesterol and triglycerides before initiating treatment and 1 and 2 months after initiating, and periodically thereafter.  Initiation or increase of the dose of lipid-lowering agents might be required on these patients.', '2', '"Product Information. Lorbrena (lorlatinib)." Pfizer U.S. Pharmaceuticals Group  (2018):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (4499, 13897, 'Lorlatinib', 'Hyperlipidemias', 'Increases in serum cholesterol and triglycerides have been reported in patients receiving lorlatinib.  Grade 3 or 4 elevations in total cholesterol occurred in 17% and Grade 3 or 4 elevations in triglycerides occurred in 17% of the patients enrolled in a clinical trial with up to 80% percent of patients requiring initiation of lipid-lowering medications.  Caution is advised in patients with hyperlipidemias.  Monitor serum cholesterol and triglycerides before initiating treatment and 1 and 2 months after initiating, and periodically thereafter.  Initiation or increase of the dose of lipid-lowering agents might be required on these patients.', '2', '"Product Information. Lorbrena (lorlatinib)." Pfizer U.S. Pharmaceuticals Group  (2018):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (4500, 11849, 'Lorlatinib', 'Hypertension', 'The use of lorlatinib might cause hypertension.  It is recommended to assess and control blood pressure prior to treatment initiation.  Caution should be exercised when treating patients with prior history of hypertension, uncontrolled hypertension, and those at risk.  Monitor blood pressure after 2 weeks and at least monthly thereafter during treatment.  Withhold and resume lorlatinib at a reduced dose or permanently discontinue treatment based on severity.', '2', '"Product Information. Lorbrena (lorlatinib)." Pfizer U.S. Pharmaceuticals Group  (2018):', 'DDInter', 0);
