INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (4501, 13897, 'Lorlatinib', 'Hypertension', 'The use of lorlatinib might cause hypertension.  It is recommended to assess and control blood pressure prior to treatment initiation.  Caution should be exercised when treating patients with prior history of hypertension, uncontrolled hypertension, and those at risk.  Monitor blood pressure after 2 weeks and at least monthly thereafter during treatment.  Withhold and resume lorlatinib at a reduced dose or permanently discontinue treatment based on severity.', '2', '"Product Information. Lorbrena (lorlatinib)." Pfizer U.S. Pharmaceuticals Group  (2018):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (4502, 11849, 'Lorlatinib', 'Lung Diseases, Interstitial', 'Patients treated with lorlatinib have reported severe, life-threatening, or fatal interstitial lung disease (ILD) and/or pneumonitis.  It is recommended to investigate and monitor patients for pulmonary symptoms indicative of ILD/pneumonitis such as hypoxia, cough, dyspnea, or interstitial infiltrates on radiologic exams.  Withhold therapy for for suspected ILD/pneumonitis and permanently discontinue therapy for treatment-related ILD/pneumonitis of any severity.', '2', '"Product Information. Lorbrena (lorlatinib)." Pfizer U.S. Pharmaceuticals Group  (2018):', 'DDInter', 0);
