INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (4668, 14322, 'Aprepitant', 'Liver Diseases', 'Aprepitant is primarily metabolized by the liver.  In studies of patients with mild to moderate hepatic impairment (Child-Pugh score 5 to 9), aprepitant was well tolerated and no clinically significant alterations in its pharmacokinetics were observed.  Thus, no dosage adjustment of aprepitant is necessary in this patient population.  However, there are no clinical or pharmacokinetic data in patients with severe hepatic impairment (Child-Pugh score > 9), and caution is advised if aprepitant is used in such patients.', '2', '"Product Information. Emend (aprepitant)." Merck & Co., Inc  (2003):', 'DDInter', 0);
