INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (9174, 0, 'Moricizine', 'Water-Electrolyte Imbalance', 'Electrolyte imbalance can alter the therapeutic effectiveness of antiarrhythmic agents.  Hypokalemia and hypomagnesemia can reduce the effectiveness of antiarrhythmic agents.  In some cases, these disorders can exaggerate the degree of QTc prolongation and increase the potential for torsade de pointes.  Hyperkalemia can potentiate the toxic effects of antiarrhythmic agents.  Electrolyte imbalance should be corrected prior to initiating antiarrhythmic therapy.  Clinical monitoring of cardiac function and electrolyte concentrations is recommended.', '2', '"Product Information. Tonocard (tocainide)." Merck & Co., Inc  (2002):|"Product Information. Ethmozine (moricizine)." DuPont Pharmaceuticals  (2002):|"Product Information. Cordarone (amiodarone)." Wyeth-Ayerst Laboratories  (2002):|"Product Information. Xylocaine (lidocaine)." Astra-Zeneca Pharmaceuticals  (2002):|"Product Information. Procan SR (procainamide)." Parke-Davis  (2001):|"Product Information. Pronestyl (procainamide)." Apothecon Inc  (2001):|"Product Information. Quinidex Extentabs (quiNIDine)." Wyeth-Ayerst Laboratories|"Product Information. Tambocor (flecainide)." 3M Pharmaceuticals  (2001):|"Product Information. Mexitil (mexiletine)." Boehringer-Ingelheim  (2001):|"Product Information. Rythmol (propafenone)." Knoll Pharmaceutical Company|"Product Information. Norpace (disopyramide)." Searle  (2001):|"Product Information. Cordarone (amiodarone)." Apothecon Inc  (2022):|"Product Information. Corvert (ibutilide)." Pharmacia and Upjohn  (2001):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (9175, 10102, 'Asenapine', 'Liver Failure', 'Asenapine is contraindicated in patients with severe hepatic impairment (Child-Pugh C).  Asenapine is a high clearance drug and its exposure has shown to be 7-fold higher in subjects with severe hepatic impairment than the exposure observed in subjects with normal hepatic function.  No dosage adjustment for asenapine is required in patients with mild to moderate hepatic impairment (Child-Pugh A and B) because the drug exposure is similar to that in subjects with normal hepatic function.', '3', '"Product Information. Saphris (asenapine)." Schering-Plough Corporation  (2009):', 'DDInter', 0);
