INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (9338, 180, 'Moxifloxacin', 'Liver Diseases', 'Moxifloxacin is primarily metabolized by the liver via glucuronide and sulfate conjugation and may accumulate in patients with impaired hepatic function.  Therapy with moxifloxacin should be administered cautiously in patients with liver disease.  Dosage adjustments are not necessary in patients with mild hepatic insufficiency (Child Pugh Class A).  Due to a lack of clinical data, use is not recommended in patients with moderate or severe hepatic insufficiency.', '3', '"Product Information. Avelox (moxifloxacin)." Bayer  (2001):', 'DDInter', 0);
