INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (9444, 180, 'Moxifloxacin', 'Myasthenia Gravis', 'Fluoroquinolones have neuromuscular blocking activity and may exacerbate muscle weakness in persons with myasthenia gravis.  Postmarketing serious adverse events, including deaths and requirement for ventilatory support, have been associated with fluoroquinolones use in persons with myasthenia gravis.  Fluoroquinolones should be avoided in patients with history of myasthenia gravis.', '3', '"Product Information. Cipro (ciprofloxacin)." Bayer  (2002):|"Product Information. Noroxin (norfloxacin)." Merck & Co., Inc  (2001):|"Product Information. Floxin (ofloxacin)." Ortho McNeil Pharmaceutical  (2001):|"Product Information. Levaquin (levofloxacin)." Ortho McNeil Pharmaceutical  (2001):|"Product Information. Raxar (grepafloxacin)." Glaxo Wellcome  (2001):|"Product Information. Factive (gemifloxacin)." *GeneSoft Inc  (2003):|"Product Information. Baxdela (delafloxacin)." Melinta Therapeutics, Inc.  (2017):', 'DDInter', 0);
