INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (9807, 23264, 'Naratriptan', 'Liver Diseases', 'The manufacturer considers the use of naratriptan to be contraindicated in patients with severe hepatic impairment (Child-Pugh grade C).  Naratriptan is partially metabolized by the liver.  In patients with moderate hepatic impairment (Child-Pugh grade A or B), the clearance of naratriptan has been shown to decrease by 30% compared to healthy controls, resulting in an approximately 40% increase in the mean half-life.  A lower initial dosage should be considered in patients with mild or moderate hepatic impairment, and the maximum dosage should not exceed 2.5 mg per 24-hour period.  There are no data concerning the use of naratriptan in patients with severe hepatic impairment, thus it is not recommended.', '3', '"Product Information. Amerge (naratriptan)." Glaxo Wellcome  (2001):', 'DDInter', 0);
