INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (9809, 23264, 'Naratriptan', 'Kidney Diseases', 'The manufacturer considers the use of naratriptan to be contraindicated in patients with severe renal impairment (CrCl < 15 mL/min).  Naratriptan is primarily eliminated by the kidney, approximately 50% as parent drug and 30% as metabolites.  In patients with moderate renal impairment (CrCl = 18 to 39 mL/min), the clearance of naratriptan has been shown to decrease by 50% compared to healthy controls, resulting in an approximately 83% increase in the mean half-life.  A lower initial dosage should be considered in patients with mild or moderate renal impairment, and the maximum dosage should not exceed 2.5 mg per 24-hour period.  There are no data concerning the use of naratriptan in patients with severe renal impairment, thus it is not recommended.', '3', '"Product Information. Amerge (naratriptan)." Glaxo Wellcome  (2001):', 'DDInter', 0);
