INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (9820, 17536, 'Nateglinide', 'Liver Diseases', 'Nateglinide is primarily metabolized in the liver to both pharmacologically active and inactive substances.  Patients with impaired liver function may be exposed to higher concentrations of nateglinide and its associated metabolites.  Higher serum drug levels, in addition to diminished gluconeogenic capacity secondary to hepatic insufficiency, may increase the potential for severe hypoglycemic episodes.  Therapy with nateglinide should be administered cautiously in patients with liver disease.  Dosage adjustments may be necessary in moderate to severe hepatic impairment, although clinical data are lacking.', '2', '"Product Information. Starlix (nateglinide)." Novartis Pharmaceuticals  (2001):', 'DDInter', 0);
