INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (9823, 721, 'Naltrexone', 'Liver Diseases', 'The use of naltrexone is contraindicated in patients with active hepatitis or hepatic failure.  Naltrexone has caused direct hepatocellular injury when administered at doses less than or equal to 5 times the recommended once daily 50 mg dose.  Therapy with naltrexone should be administered cautiously in patients with active hepatic disease.  Clinical monitoring of transaminase levels prior to and during naltrexone therapy  is recommended.', '3', 'Verebey K, Volavka J, Mule SJ, Resnick RB "Naltrexone: disposition, metabolism, and effects after acute and chronic dosing." Clin Pharmacol Ther 20 (1976):  315-28|"Product Information. ReVia (naltrexone)." DuPont Pharmaceuticals  (2001):|Volpicelli JR, Volpicelli LA, Obrien CP "Medical management of alcohol dependence: clinical use and limitations of naltrexone treatment." Alcohol Alcohol 30 (1995):  789-98', 'DDInter', 0);
