INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, source) VALUES (4939, 0, 'Cefixime', 'Diabetes Mellitus', 'The cephalosporins, cefixime and cefdinir, have been reported to cause false-positive reactions for ketones in the urine with tests using nitroprusside, but not with tests using nitroferricyanide.  Clinicians should be cognizant of this interaction when prescribing or administering either of these agents to patients with diabetes.', '2', 'DDInter');
