INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, source, created_at) VALUES (5032, 0, 'Cefprozil', 'Phenylketonurias', 'Cefzil (brand of cefprozil) oral suspension contains 28 mg of phenylalanine per each 5 mL of reconstituted suspension for both the 125 mg/5 mL and 250 mg/5mL strengths.  The phenylalanine content should be considered when these products are used in patients who must restrict their intake of phenylalanine (i.e. phenylketonurics).', '2', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, source, created_at) VALUES (5033, 0, 'Cefprozil', 'Diseases requiring dialysis', 'Most cephalosporin antibiotics are removed by hemodialysis.  Doses should either be scheduled for administration after dialysis or supplemental doses be given after dialysis.  Cefonicid, cefixime, and ceftriaxone are not significantly removed by hemodialysis.', '2', 'DDInter', 0);
