INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, source, created_at) VALUES (5078, 0, 'Cefuroxime', 'Diseases requiring dialysis', 'Cefuroxime serum levels can be reduced by hemodialysis and peritoneal dialysis.  A supplemental dose should be given at the end of each hemodialysis session.  There is not enough information regarding the use of cefuroxime in patients on peritoneal dialysis.', '2', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, source, created_at) VALUES (5079, 0, 'Cefuroxime', 'Diabetes Mellitus', 'A false-negative result for blood/plasma glucose may occur with ferricyanide tests in patients receiving cefuroxime; either the glucose oxidase or hexokinase method should be used to determine blood/plasma glucose levels during cefuroxime therapy.  Clinicians should consider the interaction with the ferricyanide test when prescribing or administering cefuroxime to patients with diabetes.', '2', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, source, created_at) VALUES (5080, 0, 'Cefuroxime', 'Epilepsy', 'Several cephalosporins (including cefuroxime) have been implicated in triggering seizures, especially in patients with renal dysfunction when the dosage was not reduced.  If seizures associated with drug therapy occur, cefuroxime should be discontinued.  Anticonvulsant therapy can be administered if clinically indicated.', '2', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, source, created_at) VALUES (5081, 0, 'Cefuroxime', 'Heart Failure', 'Parenteral cefuroxime sodium contains approximately 54.2 mg (2.4 mEq) of sodium per gram of cefuroxime activity.  The sodium content should be considered in patients with conditions that may require sodium restriction, such as congestive heart failure, hypertension, and fluid retention.', '2', 'DDInter', 0);
