INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (5041, 3797, 'Magnesium sulfate', 'Myasthenia Gravis', 'The use of magnesium sulfate is contraindicated in patients with myasthenia gravis as it can precipitate a myasthenic crisis.  Myasthenic crisis is a life-threatening condition characterized by neuromuscular respiratory failure.  Symptoms of myasthenic crisis may include difficulty swallowing, ptosis, facial droop, weakness and/or difficulty breathing that may require intubation.', '3', '"Product Information. Magnesium Sulfate (magnesium sulfate)." Abbott Pharmaceutical  (2001):', 'DDInter', 0);
