INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (5217, 7659, 'Mefenamic acid', 'Exanthema', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) can cause serious skin adverse reactions (e.g., Stevens-Johnson syndrome, toxic epidermal necrolysis, and exfoliative dermatitis), which can be fatal.  These serious events may occur without warning.  Patients should be advised to discontinue the NSAID and seek medical attention promptly at the first sign of skin rash or any other sign of hypersensitivity.  NSAIDs are contraindicated in patients with previous serious skin reactions to these drugs.', '3', '"Product Information. Motrin (ibuprofen)." Pharmacia and Upjohn  (2002):|"Product Information. Nalfon (fenoprofen)." Xspire Pharma  (2002):|"Product Information. Indocin (indomethacin)." Merck & Co., Inc  (2002):|"Product Information. Orudis (ketoprofen)." Wyeth-Ayerst Laboratories  (2002):|"Product Information. Naprosyn (naproxen)." Syntex Laboratories Inc  (2002):|"Product Information. Clinoril (sulindac)." Merck & Co., Inc  (2001):|"Product Information. Tolectin (tolmetin)." McNeil Pharmaceutical  (2001):|"Product Information. Voltaren (diclofenac)." Novartis Pharmaceuticals  (2001):|"Product Information. Relafen (nabumetone)." SmithKline Beecham  (2001):|"Product Information. Feldene (piroxicam)." Pfizer U.S. Pharmaceuticals  (2001):|"Product Information. Ansaid (flurbiprofen)." Pharmacia and Upjohn  (2001):|"Product Information. Lodine (etodolac)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Daypro (oxaprozin)." Searle  (2001):|"Product Information. Celebrex (celecoxib)." Searle  (2001):|"Product Information. Mobic (meloxicam)." Boehringer-Ingelheim  (2001):|"Product Information. Meclofenamate Sodium (meclofenamate)." Mylan Pharmaceuticals Inc  (2012):', 'DDInter', 0);
