INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (11001, 8002, 'Nitrofurantoin', 'Peripheral Nervous System Diseases', 'The use of nitrofurantoin has occasionally been associated with the development of peripheral neuropathy, which may be severe and irreversible.  Patients at risk include the elderly and those with renal impairment, anemia, diabetes mellitus, electrolyte imbalance, vitamin B12 deficiency, diabetes, and/or debilitating diseases.  Therapy with nitrofurantoin should be administered cautiously in patients with one or more risk factors and/or preexisting peripheral neuropathy.  Periodic monitoring of renal function is recommended during prolonged therapy.', '2', 'White WT, Harrison L, Dumas J "Nitrofurantoin unmasking peripheral neuropathy in a type 2 diabetic patient." Arch Intern Med 144 (1984):  821|Yiannikas C, Pollard JD, McLeod JG "Nitrofurantoin neuropathy." Aust N Z J Med 11 (1981):  400-5|Craven RS "Furadantin neuropathy." Aust N Z J Med 3 (1971):  246-9', 'DDInter', 0);
