INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (11099, 3424, 'Nitroglycerin', 'Pericarditis', 'The use of intravenous nitroglycerin is contraindicated in patients with constrictive pericarditis, pericardial tamponade, or restrictive cardiomyopathy.  In these patients, cardiac output is dependent upon venous return, which can be reduced by nitroglycerin due to venous pooling.  Also, nitroglycerin-induced hypotension may lead to paradoxical bradycardia and increased angina pectoris.', '3', '"Product Information. Tridil (nitroglycerin)." DuPont Pharmaceuticals  (2002):', 'DDInter', 0);
