INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (11253, 4278, 'Nifedipine', 'Intestinal Obstruction', 'The extended-release formulation of nifedipine (Procardia XL) contains a non-deformable material.  There have been rare reports of obstructive symptoms in patients with known strictures following the ingestion of similar sustained-release products.  Therapy with the extended-release formulation of nifedipine should be administered cautiously in patients with preexisting severe gastrointestinal narrowing or obstruction, whether pathologic or iatrogenic.', '2', '"Product Information. Procardia (nifedipine)." Pfizer U.S. Pharmaceuticals  (2002):|Smitz S, Bonnet V, Delporte JP "Severe gastrointestinal dysfunction and retention of extended release nifedipine tablets." J Am Geriatr Soc 46 (1998):  656-7', 'DDInter', 0);
