INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (11259, 0, 'Nifurtimox', 'Liver Diseases', 'The effect of hepatic impairment on the pharmacokinetics of nifurtimox is unknown.  Administer under close medical supervision on these patients.', '2', '"Product Information. Lampit (nifurtimox)." Bayer Pharmaceutical Inc  (2020):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (11260, 0, 'Nifurtimox', 'Nervous System Diseases', 'Patients with a history of brain injury, seizures, psychiatric disease or behavioral alterations may experience worsening of their conditions when receiving treatment with nifurtimox.  This drug should be administered under close medical supervision in these patients.', '2', '"Product Information. Lampit (nifurtimox)." Bayer Pharmaceutical Inc  (2020):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (11261, 0, 'Nifurtimox', 'Porphyrias', 'Treatment with nifurtimox may precipitate attacks of porphyria and should be used under close medical supervision in patients with this diagnosis.', '2', '"Product Information. Lampit (nifurtimox)." Bayer Pharmaceutical Inc  (2020):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (11262, 0, 'Nifurtimox', 'Kidney Diseases', 'The effect of renal impairment on the pharmacokinetics of nifurtimox is unknown, but studies suggest that the blood concentrations of the drug are increased in patients with end-stage renal disease that require hemodialysis.  Administer under close medical supervision on these patients.', '2', '"Product Information. Lampit (nifurtimox)." Bayer Pharmaceutical Inc  (2020):', 'DDInter', 0);
