INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (11265, 23613, 'Nitroprusside', 'Hypertension', 'Nitroprusside should not be used for the treatment of compensatory hypertension, such as that associated with aortic coarctation or arteriovenous shunt.', '3', '"Product Information. Nipride RTU (sodium nitroprusside)." Roche Laboratories', 'DDInter', 0);
