INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (11269, 23613, 'Nitroprusside', 'Sepsis', 'Sodium nitroprusside should not be used for the treatment of acute congestive heart failure associated with reduced peripheral vascular resistance such as high- output heart failure that may be seen in endotoxic sepsis.', '3', '"Product Information. Nipride RTU (sodium nitroprusside)." Roche Laboratories', 'DDInter', 0);
