INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (11273, 23613, 'Nitroprusside', 'Hepatic Insufficiency', 'Use caution when administering sodium nitroprusside to patients with hepatic insufficiency.', '2', '"Product Information. Nipride RTU (sodium nitroprusside)." Roche Laboratories', 'DDInter', 0);
