INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (11275, 23613, 'Nitroprusside', 'Intracranial Hypertension', 'Like other vasodilators, sodium nitroprusside can cause increases in intracranial pressure.  In patients whose intracranial pressure is already elevated, sodium nitroprusside should be used only with extreme caution.', '2', '"Product Information. Nipride RTU (sodium nitroprusside)." Roche Laboratories', 'DDInter', 0);
