INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (11305, 24133, 'Nivolumab', 'Diabetes Mellitus', 'Nivolumab can cause type 1 diabetes mellitus.  Monitor for hyperglycemia or other signs and symptoms of diabetes.  Withhold treatment in cases of severe hyperglycemia until metabolic control is achieved.  Permanently discontinue nivolumab for life-threatening hyperglycemia.  If appropriate modify the dose according to manufacturer recommendations when nivolumab is administered in combination with ipilimumab, and if nivolumab is withheld, ipilimumab should also be withheld.  Care should be exercised when using nivolumab in diabetic patients.', '2', '"Product Information. Opdivo (nivolumab)." Bristol-Myers Squibb  (2014):', 'DDInter', 0);
