INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (11315, 24133, 'Nivolumab', 'Thyroid Diseases', 'Nivolumab can cause autoimmune thyroid disorders.  Monitor thyroid function prior to and periodically during treatment.  Administer hormone-replacement therapy for hypothyroidism.  Initiate medical management for control of hyperthyroidism.  There are no recommended dose adjustments of nivolumab for hypothyroidism or hyperthyroidism.  Care should be taken when using this agent in patients with thyroid disease.', '2', '"Product Information. Opdivo (nivolumab)." Bristol-Myers Squibb  (2014):', 'DDInter', 0);
