INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (11317, 24133, 'Nivolumab', 'Myasthenia Gravis', 'Certain PD-1 inhibitors such as nivolumab and pembrolizumab should be used with caution in patients with myasthenia gravis (MG).  Worsening of myasthenic weakness has been reported in people with previously diagnosed MG.  It is recommended to advise patients with MG and cancer considering cancer immunotherapy about this possible side effect.', '2', '"Product Information. Keytruda (pembrolizumab)." Merck & Co., Inc  (2014):|"Product Information. Opdivo (nivolumab)." Bristol-Myers Squibb  (2014):', 'DDInter', 0);
