INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (11649, 1079, 'Atomoxetine', 'Bipolar Disorder', 'Central nervous system (CNS) stimulants may induce a mixed/manic episode in patients with bipolar disorder.  Prior to initiating treatment, screen patients for risk factors for developing a manic episode (e.g., comorbid or history of depressive symptoms or a family history of suicide, bipolar disorder, and depression).  Close monitoring is recommended when using these agents in patients with bipolar disorders.', '2', '"Product Information. Fastin (phentermine)." SmithKline Beecham  (2001):|"Product Information. Cylert (pemoline)." Abbott Pharmaceutical  (2001):|"Product Information. Ritalin (methylphenidate)." Novartis Pharmaceuticals  (2001):|"Product Information. Desoxyn (methamphetamine)." Abbott Pharmaceutical  (2001):|"Product Information. Dexedrine (dextroamphetamine)." SmithKline Beecham  (2001):|"Product Information. Adderall (amphetamine-dextroamphetamine)." Shire Richwood Pharmaceutical Company Inc  (2001):|"Product Information. Didrex (benzphetamine)." Pharmacia and Upjohn  (2001):|"Product Information. Prelu-2 (phendimetrazine)." Boehringer-Ingelheim  (2001):|"Product Information. Tenuate (diethylpropion)." Aventis Pharmaceuticals  (2001):|"Product Information. Sanorex (mazindol)." Novartis Pharmaceuticals  (2001):|"Product Information. Focalin (dexmethylphenidate)." Mikart Inc  (2001):|"Product Information. Vyvanse (lisdexamfetamine)." Shire US Inc  (2007):', 'DDInter', 0);
