INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (11779, 23930, 'Ocrelizumab', 'Hepatitis B', 'The use of ocrelizumab is contraindicated in patients with an active hepatitis B virus (HBV) infection.  It is recommended to consult a liver disease expert before starting and during treatment with ocrelizumab in patients who are negative for surface antigen [HBsAg] and positive for HB core antibody [HBcAb+] or are carriers of HBV [HBsAg+].', '3', '"Product Information. Ocrevus (ocrelizumab)." Genentech  (2017):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (11780, 23930, 'Ocrelizumab', 'Breast Neoplasms', 'Ocrelizumab may increase the risk of malignancies, including breast cancer.  It is recommended to follow standard breast cancer screening guidelines.  Exercise care when using this agent in patients with a history of breast cancer or other type of malignancy.  Monitor closely for the development of any sign or symptom suggestive of a malignancy and treat appropriately.', '2', '"Product Information. Ocrevus (ocrelizumab)." Genentech  (2017):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (11781, 23930, 'Ocrelizumab', 'Infections', 'Ocrelizumab may increase the risk for upper respiratory tract infections, lower respiratory tract infections, skin infections, and herpes-related infections.  It is recommended to delay the use of ocrelizumab in patients with an active infection until the infection is resolved.', '2', '"Product Information. Ocrevus (ocrelizumab)." Genentech  (2017):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (11782, 10188, 'Octreotide', 'Kidney Diseases', 'Octreotide is eliminated by the kidney.  Approximately 30% of octreotide is excreted unchanged in the urine.  The serum concentration of octreotide and the half-life is increased in patients with severe renal dysfunction.  Therapy with octreotide should be administered cautiously and dosage modification considered in patients with severe renal impairment as the half-life of octreotide may be increased, necessitating adjustment of the dosage.', '3', '"Product Information. Sandostatin (octreotide)." Sandoz Pharmaceuticals Corporation  (2001):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (11783, 12372, 'Octreotide', 'Kidney Diseases', 'Octreotide is eliminated by the kidney.  Approximately 30% of octreotide is excreted unchanged in the urine.  The serum concentration of octreotide and the half-life is increased in patients with severe renal dysfunction.  Therapy with octreotide should be administered cautiously and dosage modification considered in patients with severe renal impairment as the half-life of octreotide may be increased, necessitating adjustment of the dosage.', '3', '"Product Information. Sandostatin (octreotide)." Sandoz Pharmaceuticals Corporation  (2001):', 'DDInter', 0);
