INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (11837, 741, 'Olmesartan', 'Angioedema', 'The use of these agents is contraindicated in patients with a history of angioedema related to previous angiotensin-converting enzyme (ACE) inhibitor or angiotensin II receptor blocker (ARB) therapy or in patients with hereditary angioedema.  Patients with a history of angioedema unrelated to these agents may be at increased risk of angioedema while receiving angiotensin II receptor (AR) antagonists.  Patients should be advised to immediately report any signs or symptoms suggestive of angioedema (swelling of face, extremities, eyes, lips, or tongue, or difficulty swallowing or breathing) and to stop taking the medication until otherwise directed by their physician.  Emergency therapy and/or measures to prevent airway obstruction are required for angioedema involving the tongue, glottis, or larynx.  Treatment with angiotensin II receptor (AR) antagonists should be discontinued permanently if angioedema develops in association with therapy.', '3', '"Product Information. Cozaar (losartan)." Merck & Co., Inc  (2001):|"Product Information. Diovan (valsartan)." Novartis Pharmaceuticals  (2001):|"Product Information. Avapro (irbesartan)." Bristol-Myers Squibb  (2001):|"Product Information. Teveten (eprosartan)." SmithKline Beecham  (2001):|"Product Information. Atacand (candesartan)." Astra-Zeneca Pharmaceuticals  (2001):|"Product Information. Micardis (telmisartan)." Boehringer-Ingelheim  (2001):|"Product Information. Benicar (olmesartan)." Sankyo Pharma  (2002):|"Product Information. Edarbi (azilsartan)." Takeda Pharmaceuticals America  (2011):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (11838, 744, 'Olmesartan', 'Angioedema', 'The use of these agents is contraindicated in patients with a history of angioedema related to previous angiotensin-converting enzyme (ACE) inhibitor or angiotensin II receptor blocker (ARB) therapy or in patients with hereditary angioedema.  Patients with a history of angioedema unrelated to these agents may be at increased risk of angioedema while receiving angiotensin II receptor (AR) antagonists.  Patients should be advised to immediately report any signs or symptoms suggestive of angioedema (swelling of face, extremities, eyes, lips, or tongue, or difficulty swallowing or breathing) and to stop taking the medication until otherwise directed by their physician.  Emergency therapy and/or measures to prevent airway obstruction are required for angioedema involving the tongue, glottis, or larynx.  Treatment with angiotensin II receptor (AR) antagonists should be discontinued permanently if angioedema develops in association with therapy.', '3', '"Product Information. Cozaar (losartan)." Merck & Co., Inc  (2001):|"Product Information. Diovan (valsartan)." Novartis Pharmaceuticals  (2001):|"Product Information. Avapro (irbesartan)." Bristol-Myers Squibb  (2001):|"Product Information. Teveten (eprosartan)." SmithKline Beecham  (2001):|"Product Information. Atacand (candesartan)." Astra-Zeneca Pharmaceuticals  (2001):|"Product Information. Micardis (telmisartan)." Boehringer-Ingelheim  (2001):|"Product Information. Benicar (olmesartan)." Sankyo Pharma  (2002):|"Product Information. Edarbi (azilsartan)." Takeda Pharmaceuticals America  (2011):', 'DDInter', 0);
