INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (6133, 614, 'Metformin', 'Vitamin B 12 Deficiency', 'Metformin may interfere with vitamin B12 (cyanocobalamin) absorption from the B12-intrinsic factor complex.  A decrease to subnormal levels of previously normal serum B12 levels has been reported in approximately 7% of patients treated with metformin during controlled clinical trials.  Although the decrease is generally well-tolerated and rarely associated with clinical manifestations such as megaloblastic anemia, caution may be warranted when metformin therapy is administered in patients with preexisting B12 deficiency.  Vitamin B12 supplementation as well as annual measurements of hematologic parameters may be appropriate.', '2', 'Tomkin GH "Metformin and B 12 malabsorption." Ann Intern Med 76 (1972):  668|Callaghan TS, Hadden DR, Tomkin GH "Megaloblastic anaemia due to vitamin B12 malabsorption associated with long-term metformin treatment." Br Med J 280 (1980):  1214-5|Stowers JM, Smith OA "Vitamin B 12 and metformin." Br Med J 3 (1971):  246-7|Tomkin GH, Hadden DR, Weaver JA, Montgomery DA "Vitamin-B12 status of patients on long-term metformin therapy." Br Med J 2 (1971):  685-7|"Product Information. Glucophage (metformin)." Bristol-Myers Squibb  (2001):|Deutsch JC, Santhosh-Kumar CR, Kolhouse JF "Efficacy of metformin in non-insulin-dependent diabetes mellitus." N Engl J Med 334 (1996):  269', 'DDInter', 0);
