INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (6248, 5019, 'Methotrexate', 'Liver Diseases', 'The use of methotrexate is contraindicated as treatment for psoriasis or rheumatoid arthritis in patients with alcoholism, alcoholic liver disease or other chronic liver diseases.  Methotrexate causes hepatotoxicity, fibrosis and cirrhosis, usually after long-term therapy.  Fibrosis and cirrhosis may not be preceded by symptoms or abnormal liver function tests.  If methotrexate is used, patients should be instructed to immediately report any signs or symptoms suggestive of hepatic dysfunction such as jaundice, dark urine, right upper quadrant pain, or anorexia.  Persistent liver function test abnormalities and/or depression of serum albumin may require evaluation, including a liver biopsy.', '3', '"Product Information. Rheumatrex (methotrexate)." Lederle Laboratories  (2002):|"Product Information. Methotrexate (methotrexate)." Lederle Laboratories', 'DDInter', 0);
