INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (6276, 5019, 'Methotrexate', 'Bone Marrow Failure Disorders', 'Methotrexate can induce myelosuppression causing leukopenia, thrombocytopenia, neutropenia, pancytopenia and anemia.  Therapy with methotrexate is contraindicated as treatment of psoriasis in patients with bone marrow suppression or preexisting blood dyscrasias.  Methotrexate should be discontinued immediately in patients with psoriasis or rheumatoid arthritis if there is a significant fall in blood cell counts.  If need outweighs risk, therapy with methotrexate should be administered cautiously in patients with malignancy and hematopoietic impairment.  Additionally, methotrexate should be used with extreme caution in patients with active infection, and it is usually contraindicated in patients with overt or laboratory evidence of immunodeficiency syndromes.  Patients should be instructed to immediately report any signs or symptoms suggesting bone marrow suppression or infection such as fever, sore throat, or bleeding.  Clinical monitoring of hematopoietic function is recommended.', '3', '"Product Information. Rheumatrex (methotrexate)." Lederle Laboratories  (2002):|"Product Information. Methotrexate (methotrexate)." Lederle Laboratories', 'DDInter', 0);
