INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (14003, 5888, 'Oxaliplatin', 'Arrhythmias, Cardiac', 'The use of oxaliplatin has been associated with QT prolongation, and ventricular arrhythmias including fatal Torsade de Pointes.  Therapy with oxaliplatin should be administered cautiously in patients with congestive heart failure, bradyarrhythmias, and electrolyte abnormalities.  Correct hypokalemia or hypomagnesemia prior to initiating oxaliplatin and monitor these electrolytes periodically during therapy.  Avoid oxaliplatin in patients with congenital long QT syndrome.', '3', '"Product Information. Eloxatin (oxaliplatin)." Sanofi Winthrop Pharmaceuticals  (2002):', 'DDInter', 0);
